components/common/payment-icons:
{{#with theme_settings}}
    {{#or show_accept_amex show_accept_discover show_accept_mastercard show_accept_paypal show_accept_visa}}
        <div class="footer-payment-icons">
            {{#if show_accept_amex}}
                <svg class="footer-payment-icon"><use xlink:href="#icon-logo-american-express"></use></svg>
            {{/if}}
            {{#if show_accept_discover}}
                <svg class="footer-payment-icon"><use xlink:href="#icon-logo-discover"></use></svg>
            {{/if}}
            {{#if show_accept_mastercard}}
                <svg class="footer-payment-icon"><use xlink:href="#icon-logo-mastercard"></use></svg>
            {{/if}}
            {{#if show_accept_paypal}}
                <svg class="footer-payment-icon"><use xlink:href="#icon-logo-paypal"></use></svg>
            {{/if}}
            {{#if show_accept_visa}}
                <svg class="footer-payment-icon"><use xlink:href="#icon-logo-visa"></use></svg>
            {{/if}}
        </div>
    {{/or}}
{{/with}}

components/sbthemes/common/social-links:
{{#if social_media}}

    <h5 class="footer-info-heading">{{lang 'social.connect'}}</h5>
    <ul class="socialLinks socialLinks--alt">
        {{#each social_media}}
            <li class="socialLinks-item">
                {{#if display_name '===' 'google+'}}
                    <a class="icon icon--google" href="{{url}}" target="_blank">
                        <svg><use xlink:href="#icon-google" /></svg>
                    </a>
                {{else}}
                    <a class="icon icon--{{display_name}}" href="{{url}}" target="_blank">
                        <svg><use xlink:href="#icon-{{display_name}}" /></svg>
                    </a>
                {{/if}}
            </li>
        {{/each}}
    </ul>

{{/if}}

components/sbthemes/module/sb_banner/ban:
{{#if status }}
<div class="module sb-banner sb-banner--{{prefix}} {{class_sfx}}">
	{{#if (lang (concat (concat 'sb_mainbanner.' prefix)'.heading')) '!=' ' '}}
	<h5 class="block-title"> {{lang (concat (concat 'sb_mainbanner.' prefix)'.heading')}}</h5>
	{{/if}}
	
	{{#if display_mode '==' 'horizontal'}}
		<div class="block-content clearfix d-flex flex-row">
	{{else}}
		<div class="block-content clearfix ">
	{{/if}}
	
	
	{{#if limitation '>' 1}}
		{{#for  limitation }}
			{{> components/sbthemes/module/sb_banner/banner-item 
				theme_settings = ../theme_settings
				show_content = ../showContent
				image	= (lang  (concat (concat (concat (concat 'sb_mainbanner.' ../prefix) '.banner') $index)'.image'))
				placeholder	= (lang  (concat (concat (concat (concat 'sb_mainbanner.' ../prefix) '.banner') $index)'.placeholderImage'))
				icon	= (lang  (concat (concat (concat (concat 'sb_mainbanner.' ../prefix) '.banner') $index)'.icon'))
				url		= (lang  (concat (concat (concat (concat 'sb_mainbanner.' ../prefix) '.banner') $index)'.url'))
				title	= (lang  (concat (concat (concat (concat 'sb_mainbanner.' ../prefix) '.banner') $index)'.title'))
				text	= (lang  (concat (concat (concat (concat 'sb_mainbanner.' ../prefix) '.banner') $index)'.text'))
				button	= (lang  (concat (concat (concat (concat 'sb_mainbanner.' ../prefix) '.banner') $index)'.button'))
			}}
			
		{{/for}}
	{{else}}
		{{> components/sbthemes/module/sb_banner/banner-item 
			show_content = ../showContent
			image	= (lang    (concat (concat 'sb_mainbanner.' ../prefix) '.banner1.image'))
			placeholder	= (lang    (concat (concat 'sb_mainbanner.' ../prefix) '.banner1.placeholderImage'))
			icon	= (lang    (concat (concat 'sb_mainbanner.' ../prefix) '.banner1.icon'))
			url		= (lang    (concat (concat 'sb_mainbanner.' ../prefix) '.banner1.url'))
			title	= (lang    (concat (concat 'sb_mainbanner.' ../prefix) '.banner1.title'))
			text	= (lang    (concat (concat 'sb_mainbanner.' ../prefix) '.banner1.text'))
			button	= (lang    (concat (concat 'sb_mainbanner.' ../prefix) '.banner1.button'))
		}}
	{{/if}}
	</div>
</div>
{{/if}}

components/sbthemes/module/sb_facebook_m:
{{#if status }}

<div id="fb-root"></div>
<script>(function(d, s, id) {
  var js, fjs = d.getElementsByTagName(s)[0];
  if (d.getElementById(id)) return;
  js = d.createElement(s); js.id = id;
  js.src = "//connect.facebook.net/en_US/sdk.js#xfbml=1&version=v2.10";
  fjs.parentNode.insertBefore(js, fjs);
}(document, 'script', 'facebook-jssdk'));</script>

<div class="sb-fb-message {{theme_settings.fbMessage_pos }}" style="width:{{lang 'sb_themes.sb_facebook_message.width' }}px">
	<div class="sb-fb-ms-inner" style="display: none;">
		<div class="offline_heading" >
            <label class="button_chat_offline_text">{{lang 'sb_themes.sb_facebook_message.widget_text' }}</label>
            <i class="fa fa-angle-down shrink_icon"></i>
        </div>
		<div class="fb-page" 
			data-href="{{theme_settings.fbMessage_url}}" 
			data-tabs="{{lang 'sb_themes.sb_facebook_message.display_tabs'}}" 
			data-width="{{lang 'sb_themes.sb_facebook_message.width' }}" 
			data-height="{{lang 'sb_themes.sb_facebook_message.height' }}" 
			data-small-header="true" 
			data-adapt-container-width="true" 
			data-hide-cover="true" 
			data-show-facepile="true">
			<blockquote cite="{{theme_settings.fbMessage_url}}" class="fb-xfbml-parse-ignore">
				<a href="{{theme_settings.fbMessage_url}}">{{lang 'sb_themes.sb_facebook_message.heading'}}</a>
			</blockquote>
		</div>
	</div>
	
	<div class="sb-fb-ms-bottom sb-fb-message-style-{{lang 'sb_themes.sb_facebook_message.button_style'}} ">
		{{#if (lang 'sb_themes.sb_facebook_message.button_style') '==' 'default'}}
      	<div class="sb-fb-ms-heading sb-fb-message-head">
      		<img class="online" src="{{cdn 'img/sb_facebook_message/da.png'}}"> {{lang 'sb_themes.sb_facebook_message.widget_text' }}
      	{{ else if (lang 'sb_themes.sb_facebook_message.button_style') '==' 'metro' }}
      	<div class="sb-fb-ms-heading sb-fb-message-head-metro">
      		<a href="javascript:void(0)"><span></span>{{lang 'sb_themes.sb_facebook_message.widget_text' }}</a>
		{{ else if (lang 'sb_themes.sb_facebook_message.button_style') '==' 'icon' }}
      	<div class="sb-fb-ms-heading sb-fb-message-head-icon ">
      		<img   src="{{cdn 'img/sb_facebook_message/messenger.png'}}">
      	{{/if}}
       	</div>
	</div>	
</div>

{{/if}}
