components/sbthemes/carousel:
{{#if carousel}}
<div class="slideshow {{#if theme_settings.general_rtl '==' 'rtl' }}dir-rtl{{/if}}">
    <div class="slideCarousel"
    data-slick='{
        "dots": false,
        "arrows": true,
        "mobileFirst": false,
        "slidesToShow": 1,
        "slidesToScroll": 1,
        "autoplay": true,
        "autoplaySpeed": {{carousel.swap_frequency}}
    }'>
   
        {{#each carousel.slides}}
        <div class="heroCarousel-slide "
            style="background-image: url({{image}})" {{#if theme_settings.general_fadeimage}}data-size-image{{/if}}>
            <a href="{{url}}">
                <img class="heroCarousel-image lazyload" src="{{image}}" alt="{{alt_text}}" title="{{alt_text}}"/>
                {{#if heading}}
                    {{> components/carousel-content show_background=false}}
                {{else}}
                    {{#if text}}
                        {{> components/carousel-content show_background=false}}
                    {{else}}
                        {{#if button_text}}
                            {{> components/carousel-content show_background=false}}
                        {{/if}}
                    {{/if}}
                {{/if}}
            </a>
        </div>
        {{/each}}
    
    </div>
        
    

</div>
{{/if}}

components/sbthemes/module/sb_banner/ban:
{{#if status }}
<div class="module sb-banner sb-banner--{{prefix}} {{class_sfx}}">
	{{#if (lang (concat (concat 'sb_mainbanner.' prefix)'.heading')) '!=' ' '}}
	<h5 class="block-title"> {{lang (concat (concat 'sb_mainbanner.' prefix)'.heading')}}</h5>
	{{/if}}
	
	{{#if display_mode '==' 'horizontal'}}
		<div class="block-content clearfix d-flex flex-row">
	{{else}}
		<div class="block-content clearfix ">
	{{/if}}
	
	
	{{#if limitation '>' 1}}
		{{#for  limitation }}
			{{> components/sbthemes/module/sb_banner/banner-item 
				theme_settings = ../theme_settings
				show_content = ../showContent
				image	= (lang  (concat (concat (concat (concat 'sb_mainbanner.' ../prefix) '.banner') $index)'.image'))
				placeholder	= (lang  (concat (concat (concat (concat 'sb_mainbanner.' ../prefix) '.banner') $index)'.placeholderImage'))
				icon	= (lang  (concat (concat (concat (concat 'sb_mainbanner.' ../prefix) '.banner') $index)'.icon'))
				url		= (lang  (concat (concat (concat (concat 'sb_mainbanner.' ../prefix) '.banner') $index)'.url'))
				title	= (lang  (concat (concat (concat (concat 'sb_mainbanner.' ../prefix) '.banner') $index)'.title'))
				text	= (lang  (concat (concat (concat (concat 'sb_mainbanner.' ../prefix) '.banner') $index)'.text'))
				button	= (lang  (concat (concat (concat (concat 'sb_mainbanner.' ../prefix) '.banner') $index)'.button'))
			}}
			
		{{/for}}
	{{else}}
		{{> components/sbthemes/module/sb_banner/banner-item 
			show_content = ../showContent
			image	= (lang    (concat (concat 'sb_mainbanner.' ../prefix) '.banner1.image'))
			placeholder	= (lang    (concat (concat 'sb_mainbanner.' ../prefix) '.banner1.placeholderImage'))
			icon	= (lang    (concat (concat 'sb_mainbanner.' ../prefix) '.banner1.icon'))
			url		= (lang    (concat (concat 'sb_mainbanner.' ../prefix) '.banner1.url'))
			title	= (lang    (concat (concat 'sb_mainbanner.' ../prefix) '.banner1.title'))
			text	= (lang    (concat (concat 'sb_mainbanner.' ../prefix) '.banner1.text'))
			button	= (lang    (concat (concat 'sb_mainbanner.' ../prefix) '.banner1.button'))
		}}
	{{/if}}
	</div>
</div>
{{/if}}

