components/products/price:
{{#and price.price_range (if theme_settings.price_ranges '==' true)}}
    {{> components/products/price-range price_range=price.price_range schema_org=schema_org}}
{{else}}
	{{#if price.with_tax}}
        <div class="price-section price-section--withTax" {{#if schema_org}}itemprop="offers" itemscope itemtype="http://schema.org/Offer"{{/if}}>
            {{#if price.rrp_with_tax}}
                <span data-product-rrp-with-tax class="price price--rrp">{{price.rrp_with_tax.formatted}}</span>
            {{/if}}

			{{#if price.non_sale_price_with_tax}}
                <span data-product-non-sale-price-with-tax class="price price--rrp"> {{price.non_sale_price_with_tax.formatted}}</span>
            {{/if}}

            <span data-product-price-with-tax class="price price--withoutTax">{{price.with_tax.formatted}}</span>
            {{#if schema_org}}
                <meta itemprop="availability" content="{{product.availability}}">
                <meta itemprop="itemCondition" itemtype="http://schema.org/OfferItemCondition" content="http://schema.org/{{product.condition}}Condition">
                <div itemprop="priceSpecification" itemscope itemtype="http://schema.org/PriceSpecification">
                    <meta itemprop="price" content="{{price.with_tax.value}}">
                    <meta itemprop="priceCurrency" content="{{currency_selector.active_currency_code}}">
                    <meta itemprop="valueAddedTaxIncluded" content="true">
                </div>
            {{/if}}
            {{#if price.without_tax}}
                <abbr title="{{lang 'products.excluding_tax'}}">{{lang 'products.price_with_tax' tax_label=price.tax_label}}</abbr>
            {{/if}}
        </div>
    {{/if}}

     {{#if price.without_tax}}
        <div class="price-section price-section--withoutTax {{#if price.with_tax}}price-section--minor{{/if}}"  {{#if schema_org}}itemprop="offers" itemscope itemtype="http://schema.org/Offer"{{/if}}>

            {{#if schema_org}}
                <meta itemprop="availability" itemtype="http://schema.org/ItemAvailability" content="http://schema.org/{{#if product.pre_order}}PreOrder{{else if product.out_of_stock}}OutOfStock{{else if product.can_purchase '===' false}}OutOfStock{{else}}InStock{{/if}}">
                <meta itemprop="itemCondition" itemtype="http://schema.org/OfferItemCondition" content="http://schema.org/{{product.condition}}Condition">
                <div itemprop="priceSpecification" itemscope itemtype="http://schema.org/PriceSpecification">
                    <meta itemprop="price" content="{{price.without_tax.value}}">
                    <meta itemprop="priceCurrency" content="{{currency_selector.active_currency_code}}">
                    <meta itemprop="valueAddedTaxIncluded" content="false">
                </div>
            {{/if}}
			<span data-product-price-without-tax class="price price--withoutTax">{{price.without_tax.formatted}}</span>

			{{#if price.rrp_without_tax}}
                <span data-product-rrp-without-tax class="price price--rrp">{{price.rrp_without_tax.formatted}}</span>
            {{/if}}
			{{#if price.non_sale_price_without_tax}}
				<span data-product-price-without-tax class="price price--noSaleWithoutTax">{{price.non_sale_price_without_tax.formatted}}</span>
			{{/if}}
            {{#if price.with_tax}}
                <abbr title="{{lang 'products.excluding_tax'}}">{{lang 'products.price_without_tax' tax_label=price.tax_label}}</abbr>
            {{/if}}
        </div>
    {{/if}}
    {{#if page_type '===' 'product'}}
        {{#if price.saved}}
             <div class="price-section price-section--saving price">
                    <span class="price">{{lang 'products.you_save_opening_text'}}</span>
                    <span data-product-price-saved class="price">
                        {{price.saved.formatted}}
                    </span>
                    <span class="price">{{lang 'products.you_save_closing_bracket'}}</span>
             </div>
        {{/if}}
    {{/if}}
{{/and}}

components/products/ratings:
{{#for 1 5}}
    {{#if ../this.rating '>=' $index}}
        <span class="icon icon--ratingFull">
            <svg>
                <use xlink:href="#icon-star" />
            </svg>
        </span>
    {{else}}
        <span class="icon icon--ratingEmpty">
            <svg>
                <use xlink:href="#icon-star" />
            </svg>
        </span>
    {{/if}}
{{/for}}
{{{snippet 'product_rating'}}}

components/sbthemes/module/sb_deals/defa:
{{#if category.products }}
<div class="sb-deals-detail"
	data-slick='{
		"slidesToShow": 1,
		"slidesToScroll": 1,
		"dots": false,
		"arrows": true,
		"responsive": [
			{
				"breakpoint": 1200,
				"settings": {
					"slidesToScroll": 1,
					"slidesToShow": 1
				}
			},
			{
				"breakpoint": 1199,
				"settings": {
					"slidesToScroll": 1,
					"slidesToShow": 1
				}
			},
			{
				"breakpoint": 992,
				"settings": {
					"slidesToScroll": 1,
					"slidesToShow": 1,
					"centerMode" : false
				}
			},
			{
				"breakpoint": 767,
				"settings": {
					"slidesToScroll": 1,
					"slidesToShow": 1,
					"centerMode" : false
				}
			},
			{
				"breakpoint": 479,
				"settings": {
					"slidesToScroll": 1,
					"slidesToShow": 1,
					"centerMode" : false
				}
			}
		]
	}'
>
	
	{{#forEach category.products }}
		<article class="product-item-container">
			<div class="left-block">
				
				
				<a href="{{url}}" class="product-item-photo">
					<img class="card-image lazyload" data-sizes="auto" src="{{cdn '/assets/img/loading.svg'}}" data-src="{{getImage image 'deals_size' (cdn ../../theme_settings.default_image_product)}}" alt="{{image.alt}}" title="{{image.alt}}" width="300" height="300">
				</a>
				{{#if ../../theme_settings.settings_countdown}}
					{{> components/sbthemes/shortcode/default prefix="countdown" product_id= id content = summary }}
				{{/if}}
			</div>

			<div class="right-block">

				<div class="ratings ">
					<div class="prod-rating" data-test-info-type="productRating">
						<span class="rating--small">
							{{> components/products/ratings rating=rating}}
						</span>
					</div>
				</div>

				<h4 class="card-title">
					<a href="{{url}}">{{name}}</a>
				</h4>
				
				{{#if ../../theme_settings.show_product_brand}}
					<p class="card-text" data-test-info-type="brandName">{{brand.name}}</p>
				{{/if}}
				
				
				<!-- <div class="description">
					{{> components/sbthemes/shortcode/summary content = summary }}
					
				</div> -->

				{{#or customer (if ../../theme_settings.restrict_to_login '!==' true)}}
					{{> components/products/price price=price customer=customer}}
				{{else}}
					{{> components/common/login-for-pricing}}
				{{/or}}
				
				
			</div>
		</article>
		
	{{/forEach}}

</div>



{{else}}
	<div class="alert alert-info" role="alert" style="margin:60px 20px 0">
		<i class="fa fa-info-circle"></i> 
	    {{{lang 'sb_themes.sb_deals.noCategory' }}}
	</div>
{{/if}}

components/sbthemes/shortcode/countdown:
{{#contains textCountdown "[countdown]"}}
	<div class="countdown--box d-none d-sm-block">
		<div class="defaultCountdown defaultCountdown-{{product_id}}" data-timer="{{last (split (first (split textCountdown '[/countdown]')) '[countdown]')}}" data-countdown-id="{{product_id}}"></div>
	</div>
{{/contains}}

components/sbthemes/shortcode/default:
{{#contains content "[/shortcode]"}}
	{{#if prefix '==' 'badges'}}
		{{#contains  (last (split (first (split ../content '[/shortcode]')) '[shortcode]')) ../prefix  }}
			{{> components/sbthemes/shortcode/badges  textBadges = (last (split (first (split ../content '[/shortcode]')) '[shortcode]')) }}
		{{/contains}}
	{{/if}}
	
	{{#if prefix '==' 'countdown'}}
		{{#contains  (last (split (first (split ../content '[/shortcode]')) '[shortcode]')) ../prefix  }}
			{{> components/sbthemes/shortcode/countdown  textCountdown = (last (split (first (split ../content '[/shortcode]')) '[shortcode]')) }}
		{{/contains}}
	{{/if}}
{{/contains}}
