components/common/currency-selector:
{{#if currency_selector.currencies.length '>' 1}}
<ul class="navUser-section currencies">
    <li class="navUser-item">
        <a class="navUser-action has-dropdown" href="#" data-dropdown="currencySelection" aria-controls="currencySelection" aria-expanded="false">{{lang 'common.currency' code=currency_selector.active_currency_code}} 
        <i class="icon" aria-hidden="true"><svg><use xlink:href="#icon-chevron-down" /></svg></i></a>
        <ul class="dropdown-menu" id="currencySelection" data-dropdown-content aria-hidden="true" tabindex="-1">
            {{#each currency_selector.currencies}}
            <li class="dropdown-menu-item">
                <a href="{{{switch_url}}}">
                    {{#if is_active}}
                    <strong>{{name}}</strong>
                    {{else}}
                        {{name}}
                    {{/if}}
                </a>
            </li>
            {{/each}}
        </ul>
    </li>
</ul>
{{/if}}

components/sbthemes/module/sb_megamenu/d:
{{!--
Arguments:
	- moduleId (number): section instance index number
	- class_sfx (string): Use for CSS class styling suffix
--}}

{{#if display_mode '==' 'horizontal'}}
	{{#if status }} {{> components/sbthemes/module/sb_megamenu/navigation-menu}} {{/if}}
{{else}}
	{{#if status }} {{> components/sbthemes/module/sb_megamenu/navigation-vertical limit_item = limit_item}}{{/if}}
{{/if}}

components/sbthemes/module/sb_promotion/:
{{#if status }}
<div class="module sb-promotion sb-promotion--{{prefix}} {{class_sfx}}">
	<div class="block-content clearfix">
	{{#if limitation '>' 1}}
		<div class="sb-promotion--carousel"
           data-slick='{
				"dots": false,
				"arrows": false,
				"mobileFirst": true,
				"slidesToShow": 1,
				"slidesToScroll": 1,
				"vertical": true,
				"verticalSwiping": true,
				"autoplay": true,
				"autoplaySpeed": 5000
			}'
    	>
		{{#for  limitation }}
			{{> components/sbthemes/module/sb_promotion/default-item 
				url		= (lang  (concat (concat (concat (concat 'sb_mainbanner.' ../prefix) '.item') $index)'.url'))
				title	= (lang  (concat (concat (concat (concat 'sb_mainbanner.' ../prefix) '.item') $index)'.title'))
			}}
		{{/for}}
		</div>
	{{else}}
		{{> components/sbthemes/module/sb_promotion/default-item 
			url		= (lang    (concat (concat 'sb_mainbanner.' ../prefix) '.item1.url'))
			title	= (lang    (concat (concat 'sb_mainbanner.' ../prefix) '.item1.title'))
		}}
	{{/if}}
	</div>
</div>
{{/if}}
